import React, { useState, useEffect, useRef, useLayoutEffect} from 'react'
import './ToDoList.css'
import { db } from '../../firebase.js'
import firebase from "firebase"
import '../todo_chart/ToDoChart.css'
import {Bar} from 'react-chartjs-2';


export default function ToDoList({user}) {
     const [newTaskValue, setTask] = useState("");
     const [tasks, setTasks] = useState([]);
     const [doneTasks, setTaskDone] = useState()

    const today = new Date()
    const todayDate = String(today.getDate()).padStart(2, '0') + "_" + String(today.getMonth() + 1).padStart(2, '0');



    const addTask = (e) => {  
         e.preventDefault();
         db.collection(user.displayName).doc("ToDoList").collection(todayDate).add({
             text: newTaskValue,
             timestamp: firebase.firestore.FieldValue.serverTimestamp(),
             isDone: false
         })
         setTask('');
     }


     useEffect(() => {  //function to download task from today.
        if(user!=undefined){   
            db 
                .collection(user.displayName)
                .doc("ToDoList")
                .collection(todayDate) //todayDate
                .orderBy("timestamp", "desc")
                .onSnapshot((snapshot) =>{
                    setTasks(snapshot.docs.map(doc => ({
                        id: doc.id,
                        task: doc.data()
                        })));
                        }) 
            } 

    }, [user])


    // const useIsMount = () => { //function to return, than first render or no
    //     const isMountRef = useRef(true);
    //     useEffect(() => {
    //       isMountRef.current = false;
    //     }, []);
    //     return isMountRef.current;
    //   };


    //     const isMount = useIsMount();

    //     useEffect(() => {
    //       if (!isMount && tasks.length>=1) {
    //         let howManyTaskIsDone = 0;
    //         for(let i =0; i<=tasks.length-1; i++){
    //              if(tasks[i].task.isDone == true){
    //                 howManyTaskIsDone+=1
    //                 setTaskDone(howManyTaskIsDone);
    //                 }
    //         } 
    //       }
    //     }, [tasks]);






    const setAsDone = (e) => {
        db.collection(user.displayName).doc("ToDoList").collection(todayDate).doc(e.target.id)
        .update({isDone: true})
    }





    const GetDates = () => {
      var aryDates = [];

      for (var i = -6; i <= 0; i++) {
          var currentDate = new Date();
          currentDate.setDate(startDate.getDate() + i);
          aryDates.push(DayAsString(currentDate.getDay()) + ", " + currentDate.getDate());
      }

      return aryDates;
  }







   const DayAsString = (dayIndex) => {
      var weekdays = new Array(7);
      weekdays[0] = "Sunday";
      weekdays[1] = "Monday";
      weekdays[2] = "Tuesday";
      weekdays[3] = "Wednesday";
      weekdays[4] = "Thursday";
      weekdays[5] = "Friday";
      weekdays[6] = "Saturday";

      return weekdays[dayIndex];
  }

  var startDate = today; 
  var aryDates = GetDates(); 

//function to return today + 6 past day
const state = {
  labels: [aryDates[0]+"XDD", aryDates[1], aryDates[2],aryDates[3], aryDates[4], aryDates[5], "TODAY"],
  datasets: [
    {
      label: 'Rainfall',
      backgroundColor: 'rgba(75,192,192,1)',
      borderColor: 'rgba(0,0,0,1)',
      borderWidth: 2,
      data: [[0,100], [0,60], [0,40], [0,30], [0,70], [10,60]] 
    }
  ]
}








    return (
    <div className="toDoList__wrapper">
        <div className="toDoChart__wrapper">
 <div>
        <Bar
          data={state}
          options={{
            title:{
              display:true,
              text:'Average Rainfall per month',
              fontSize:20
            },
            legend:{
              display:true,
              position:'right'
            }
          }}
        />
      </div>
    </div>
    <div className="toDoList__list">
            <ul>
            {tasks.map((task) => (
                    <p> { task.task.isDone ?
                        <li className="toDoList__item" value={task.task.text} id={task.id} onClick={setAsDone}>{task.task.text}</li>:
                        <li value={task.task.text} id={task.id} onClick={setAsDone}>{task.task.text}</li> 
                        }
                    </p>
                )

                )}
            </ul>

        <div className="toDoList__addTask">
            <input type="text" value={newTaskValue} onChange={e => setTask(e.target.value)}></input>
            <button onClick={addTask}>Add Task</button>
    </div>
    </div>
    </div>
    )


                }
