import React, { useState, useEffect, useRef } from 'react'
import './ToDoChart.css'
import { db } from '../../firebase.js'
import firebase from "firebase"
import {Bar} from 'react-chartjs-2';
export default function ToDoList({user}) {

    const [tasks, setTasks] = useState([])
    const [doneTasks, setTaskDone] = useState(0)
    const [doneTaskPercent, setPercent] = useState(0)


    const today = new Date()
    const todayDate = String(today.getDate()).padStart(2, '0') + "_" + String(today.getMonth() + 1).padStart(2, '0'); 



    const GetDates = () => {
      var aryDates = [];

      for (var i = -6; i <= 0; i++) {
          var currentDate = new Date();
          currentDate.setDate(startDate.getDate() + i);
          aryDates.push(DayAsString(currentDate.getDay()) + ", " + currentDate.getDate());
      }

      return aryDates;
  }

    useEffect(() => {  //function to download task from today.
        if(user!=undefined){   
            db 
                .collection(user.displayName)
                .doc("ToDoList")
                .collection(todayDate) //todayDate
                .orderBy("timestamp", "desc")
                .onSnapshot((snapshot) =>{
                    setTasks(snapshot.docs.map(doc => ({
                        id: doc.id,
                        task: doc.data()
                        })));
                        }) 
            } 

    }, [user])




const useIsMount = () => { //function to return, than first render or no
    const isMountRef = useRef(true);
    useEffect(() => {
      isMountRef.current = false;
    }, []);
    return isMountRef.current;
  };


    const isMount = useIsMount();

    useEffect(() => {
      if (!isMount && tasks.length>=1) {
        let howManyTaskIsDone = 0;
        for(let i =0; i<=tasks.length-1; i++){
             if(tasks[i].task.isDone == true){
                howManyTaskIsDone+=1
                setTaskDone(howManyTaskIsDone);
                }
        } 
      }
    }, [tasks]);

function mouseOver (){

  db.collection(user.displayName).get()
  .then(snapshot => {
    snapshot.forEach(doc => {
          console.log(doc.id, " => ", doc.data());
    }
    );
});

}



   useEffect(() =>{
     setPercent(Math.round(doneTasks/tasks.length*100))

    },
    [tasks, doneTasks])


   const DayAsString = (dayIndex) => {
      var weekdays = new Array(7);
      weekdays[0] = "Sunday";
      weekdays[1] = "Monday";
      weekdays[2] = "Tuesday";
      weekdays[3] = "Wednesday";
      weekdays[4] = "Thursday";
      weekdays[5] = "Friday";
      weekdays[6] = "Saturday";

      return weekdays[dayIndex];
  }

  var startDate = today; 
  var aryDates = GetDates(); 

//function to return today + 6 past day
const state = {
  labels: [aryDates[0], aryDates[1], aryDates[2],aryDates[3], aryDates[4], aryDates[5], "TODAY " + doneTaskPercent+"%"],
  datasets: [
    {
      label: 'Rainfall',
      backgroundColor: 'rgba(75,192,192,1)',
      borderColor: 'rgba(0,0,0,1)',
      borderWidth: 2,
      data: [[0,10], [0,6], [0,4], [0,3], [0,7], [1,6], [0,doneTaskPercent]] 
    }
  ]
}

    return (
    <div onMouseEnter={mouseOver} className="toDoChart__wrapper">
 <div>
        <Bar 
          data={state}
          options={{
            title:{
              display:true,
              text:'Average Rainfall per month',
              fontSize:20
            },
            legend:{
              display:true,
              position:'right'
            }
          }}
        />
      </div>
    </div>
    )
}
