import React from 'react'
import './MainContent.css'
import {XYPlot,
    XAxis,
    YAxis,
    VerticalGridLines,
    HorizontalGridLines,
    VerticalBarSeries,
    VerticalBarSeriesCanvas,
    DiscreteColorLegend} from 'react-vis';
import '/Users/kamil/Desktop/Programowanie/ygo/node_modules/react-vis/dist/style.css';


export default class MainContent extends React.Component {
    state = {
      useCanvas: false
    };
    render() {
      const {useCanvas} = this.state;
      const BarSeries = useCanvas ? VerticalBarSeriesCanvas : VerticalBarSeries;

      return (
        <div>

          <XYPlot
            className="clustered-stacked-bar-chart-example"
            xType="ordinal"
            stackBy="y"
            width={300}
            height={300}
          >
            <DiscreteColorLegend
              style={{position: 'absolute', left: '50px', top: '10px'}}
              orientation="horizontal"
              items={[
                {
                  title: 'Apples',
                  color: '#12939A'
                }
              ]}
            />
            <VerticalGridLines />
            <HorizontalGridLines />
            <XAxis />
            <YAxis />
            <BarSeries
              cluster="2015"
              color="#12939A"
              data={[
                {x: '19.06', y: 95},
                {x: '20.06', y: 60},
                {x: '21.06', y: 0},
                {x: 'TODAY', y: 78}
              ]}
            />

            <BarSeries
              cluster="2016"
              color="#79C7E3"
              data={[
                {x: 'Q1', y: 22},
                {x: 'Q2', y: 2},
                {x: 'Q3', y: 22},
                {x: 'Q4', y: 18}
              ]}
            />
          </XYPlot>
        </div>
      );
    }
  }
