import React, { useState } from 'react'
import './ToDoList.css'
import { db } from '../../firebase.js'
import firebase from "firebase"

export default function ToDoList({user}) {
    const [newTaskValue, setTask] = useState("")

    const today = new Date()
    const todayDate = String(today.getDate()).padStart(2, '0') + " " + String(today.getMonth() + 1).padStart(2, '0');



    const addTask = (e) => {

         e.preventDefault();
         db.collection(user.displayName).doc("ToDoList").collection(todayDate).add({
             text: newTaskValue,
             timestamp: firebase.firestore.FieldValue.serverTimestamp()
         });
         setTask('');
     } 


    return (
        <div className="toDoList__wrapper">
        <div className="toDoList__list">
            <ul>
                <li>Zadanie1</li>
                <li>Zadanie2</li>
                <li>Zadanie3</li>
            </ul>
        </div>
        <div className="toDoList__addTask">
            <input type="text" value={newTaskValue} onChange={e => setTask(e.target.value)}></input>
            <button onClick={addTask}>Add Task</button>
        </div>
        </div>
    )
}
